<script>
export default {
  data: () => ({
    button: '',
  }),
  mounted() {
    if (localStorage.token) {
      console.log(localStorage.token);
      this.button = 'LOG IN';
    } else {
      this.button = 'SIGN UP';
    }
  },
  methods: {
    logout() {
      localStorage.removeItem('token');
      this.button = 'Sign up';
      this.$router.push('/login');
    },
    signup() {
      this.$router.push('/signup');
    },
    Do() {
      if (this.button === 'Log out') {
        this.logout();
      } else {
        this.signup();
      }
    },
  },
};
</script>
